    function downloadfile($URL, $Filename = false){
        $URL = @file_get_contents($URL);
        if($URL !== false){
            if($Filename){
                @mkdir(getdirectory($Filename), 0777 , true);
                file_put_contents($Filename, $URL);
                return basename($Filename);
            }
        }
        return $URL;
    }
